import React, { useState } from 'react';
import Movie from './Movie';

export default function MovieList() {
    const [movies, setMovies] = useState([]);
    const [title, setTitle] = useState('');
    const [rating, setRating] = useState(0);

    function handleTitleChange(event) {
        setTitle(event.target.value);
    }

    function handleRatingChange(event) {
        setRating(parseInt(event.target.value));
    }

    function handleSubmit(event) {
        event.preventDefault();
        if (title.trim() !== '') {
            const newId = movies.length > 0 ? movies[movies.length - 1].id + 1 : 1;
            setMovies([...movies, { id: newId, title, rating }]);
            setTitle('');
            setRating(0);
        }
    }

    function deleteMovie(id) {
        setMovies(movies.filter((item) => item.id !== id));
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <p>Titel:</p>
                <input
                    class="box"
                    type="text"
                    placeholder="Filmtitel..."
                    value={title}
                    onChange={handleTitleChange}
                />
                <div>
                <p>Betyg:</p>
                <select class="box" value={rating} onChange={handleRatingChange}>
                    <option value={0}>Välj Betyg</option>
                    <option value={1}>1 Star</option>
                    <option value={2}>2 Stars</option>
                    <option value={3}>3 Stars</option>
                    <option value={4}>4 Stars</option>
                    <option value={5}>5 Stars</option>
                </select>
                </div>
                <div>
                <button class="box" type="submit">Lägg till film</button>
                </div>
            </form>
            <ul className="movie-group">
                {movies.map(movie => (
                    <Movie
                        key={movie.id}
                        item={movie}
                        deleteMovie={deleteMovie}
                    />
                ))}
            </ul>
        </div>
    );
}
