import React, { useState, useRef} from 'react';
import Movie from './Movie';

//Exporterar egenskaper som filmen ska ha, Titel, rating och vilket ID den får i listan.
export default function Movielist() {
    const [movies, setMovies] = useState([{
        id: 1,
        title: "First Movie",
        rating: 3
    }]);
    const inputRef = useRef();

    //Funktion som gör att man kan lägga till en film och ge den ett ID
    function addMovie(event){
        if (event.keyCode === 13) {
            const newId = movies.length > 0 ? movies[movies.length - 1].id + 1 : 1;

            setMovies([...movies, {
                id: newId,
                title: inputRef.current.value
            }]);

            inputRef.current.value = "";
        }
    }

    //Funktion som tar bort en film med hjälp av att hitta filmens ID
    function deleteMovie(id) {
        setMovies(movies.filter((item) => item.id !== id));
    }

    return (
        <div>
            <input className="form-control" placeholder='Add new movie' ref={inputRef} onKeyUp={addMovie}/>
            <ul className="movie-group">
                { movies.map(movie => <Movie key={movie.id} item={movie} deleteMovie={deleteMovie} />)}
            </ul>
        </div>
    )
}
