import React, { useState, useRef } from 'react';
import RatingSelector from './RatingSelector';
import Movie from './Movie';

export default function MovieList() {
    const [movies, setMovies] = useState([
        {
            id: 1,
            title: "First Movie",
            rating: 0
        }
    ]);
    const inputRef = useRef();

    function addMovie(event) {
        if (event.keyCode === 13) {
            const newId = movies.length > 0 ? movies[movies.length - 1].id + 1 : 1;

            setMovies([...movies, {
                id: newId,
                title: inputRef.current.value,
                rating: 0 // Default rating for new movies
            }]);

            inputRef.current.value = "";
        }
    }

    function deleteMovie(id) {
        setMovies(movies.filter((item) => item.id !== id));
    }

    function updateRating(id, newRating) {
        setMovies(movies.map((movie) => {
            if (movie.id === id) {
                return { ...movie, rating: newRating };
            }
            return movie;
        }));
    }

    function updateRating(id, newRating) {
        setMovies(movies.map((movie) => {
            if (movie.id === id) {
                return { ...movie, rating: newRating };
            }
            return movie;
        }));
    }

    return (
        <div>
            {/* existing code... */}
            {movies.map(movie => (
                <Movie
                    key={movie.id}
                    item={movie}
                    deleteMovie={deleteMovie}
                    updateRating={updateRating}
                />
            ))}
        </div>
    );

    return (
        <div>
            <input className="form-control" placeholder='Add new movie' ref={inputRef} onKeyUp={addMovie}/>
            <ul className="movie-group">
                {movies.map(movie => (
                    <li key={movie.id} className="movie-group-item">
                        {movie.title}
                        <RatingSelector onChange={(newRating) => updateRating(movie.id, newRating)} />
                        <button className="btn btn-sm btn-danger float-end" onClick={() => deleteMovie(movie.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
